import React, {Component} from 'react'
import ListButton from './ListButton'
class Form extends Component {
    state = {
        src: ""
    };

    youtube_parser = (url) => {
        var regExp = /^.*((youtu.be\/)|(v\/)|(\/u\/\w\/)|(embed\/)|(watch?))??v?=?([^#&?]*).*/;
        var match = url.match(regExp);
        return (match && match[7].length === 11)
            ? match[7]
            : false;
    }

    _getLinkButton = text => this.setState({src: text})
    render() {
        let links = this.props.song;
        console.log(links);
        const name = this
            .props
            .song
            .map(a => a.name)
        console.log(name);
        console.log(this.state.src)
        const url = `https://www.youtube.com/embed/${this.youtube_parser(this.state.src)}`
        return (
            <div style={{
                textAlign: "center"
            }

            }>
                <div style={{
                    display: "inline-block"
                }}>
                    <iframe title="This is a unique title" width="420" height="345" src={url}></iframe>
                    <style>

                        /*Youtube Embed Code : Created with Codegena.com */
                    </style>
                    <div class="codegena">
                        <iframe
                            width= '200'
                            height='200'
                            src="https://www.youtube.com/embed/ffiJNSy8CBA?&theme=dark&autohide=2&cc_load_policy=1&showinfo=0&rel=0"
                            frameborder="1"></iframe>
                    </div>
                    <div style='font-size: 0.8em'>
                        <a href='http://codegena.com/generator/Youtube-Embed-Code-Generator.html'>Created by Codegena.</a>
                    </div>
                </div>
                <ListButton links={links} getLinkButton ={this._getLinkButton}/>
            </div>
        )
    }
}

export default Form;
