import React, {Component} from 'react'
import ListButton from './ListButton'
class Form extends Component {
  state = {
    src : ""
  };

  youtube_parser= (url)=>{
    var regExp = /^.*((youtu.be\/)|(v\/)|(\/u\/\w\/)|(embed\/)|(watch\?))\??v?=?([^#\&\?]*).*/;
    var match = url.match(regExp);
    return (match&&match[7].length===11)? match[7] : false;
  }

  _getLinkButton = text => this.setState({src : text})
  render() {
      let links =this.props.song;
      console.log(links);
      const name = this.props.song.map(a=> a.name)
      console.log(name);
      console.log(this.state.src)
      const url = `https://www.youtube.com/embed/${this.youtube_parser(this.state.src)}`
      return (
          <div style={{
              textAlign: "center"
          }}>
              <div style={{
                display:"inline-block"
              }}>
                  <iframe
                      title="This is a unique title"
                      width="420"
                      height="345"
                      src = {url}>
                  </iframe>

              </div>
              <ListButton links={links} 
                getLinkButton ={this._getLinkButton}
              />
          </div>    
      )
    }
}

export default Form;
